mapper.select:
SELECT s.id, s.student_id, s.real_name, s.gender, s.class_name, s.phone, s.email, s.user_id
FROM student s
WHERE s.id = #{studentId}

SELECT s.id, s.student_id, s.real_name, s.gender, s.class_name, s.phone, s.email, s.user_id
FROM student s
WHERE s.user_id = #{userId}

SELECT s.id, s.student_id, s.real_name, s.gender, s.class_name, s.phone, s.email, s.user_id
FROM student s
WHERE s.student_id = #{studentNumber}

SELECT s.id, s.student_id, s.real_name, s.gender, s.class_name, s.phone, s.email, s.user_id
FROM student s
LIMIT #{offset}, #{limit}

SELECT s.id, s.student_id, s.real_name, s.gender, s.class_name, s.phone, s.email, s.user_id
FROM student s
WHERE s.real_name = #{name}

SELECT s.id, s.student_id, s.real_name, s.gender, s.class_name, s.phone, s.email, s.user_id
FROM student s
WHERE s.class_name = #{className}
